import { useEffect, useRef, useState } from "react";

const SimpleInput = (props) => {
  const nameInputRef = useRef();
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(false);
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);

  useEffect(() => {
    enteredNameIsValid && console.log("Name input is valid");
  }, [enteredNameIsValid]);

  const nameInputChangeHandler = (e) => {
    setEnteredName(e.target.value);
  };

  const formSubmitionHandler = (e) => {
    e.preventDefault();
    /*    const enteredvalue = nameInputRef.current.value;  not ideal*/
    if (enteredName.trim() === "") {
      setEnteredNameIsValid(false);
      return;
    }
    setEnteredNameIsValid(true);
    console.log(nameInputIsInvalidValid)
    setEnteredName("");
  };

  const nameInputIsInvalidValid = enteredNameTouched && !enteredNameIsValid;

  const nameInputClasses = nameInputIsInvalidValid
    ? "form-control"
    : "form-control invalid";

  return (
    <form onSubmit={formSubmitionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>

        <input
          value={enteredName}
          ref={nameInputRef}
          onChange={nameInputChangeHandler}
          type="text"
          id="name"
        />
        {!enteredNameIsValid && (
          <p className="error-text">Name must not be empty.</p>
        )}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
