import { useEffect, useState } from "react";
import useInput from "../hooks/use-input";



const BasicForm = (props) => {

  const [formIsValid,setFormIsValid] = useState(false);




  const {
    value: nameValue,
    isValid: nameIsValid,
    hasError: nameHasError,
    valueChangeHandler: nameValueChangeHandler,
    inputBlurHandler: NameBlurHandler,
    reset:NameInputReset
  }=useInput(
    (value)=>{


      nameErrorMessage=value.trim().length>0 && value.trim().length<3 ?"Minimum character must be greater than 3":""

      return value.trim()!==""&&value.length>3
    })




    useEffect(()=>{
      if(nameIsValid){
        setFormIsValid(true);

      }
      else{
        setFormIsValid(false)
      }

    },[nameIsValid])



  const formSubmitHandler=(e)=>{
    e.preventDefault();

    if(!nameIsValid){
      return
    }

    NameInputReset();
  };

  const nameInputClasses = nameHasError
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={formSubmitHandler}>

      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input className={nameInputClasses} value={nameValue} onChange={nameValueChangeHandler} onBlur={NameBlurHandler} type='text' id='name' />
         {nameHasError&&  <p className="error-text" >{nameErrorMessage}</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' />
        </div>
      </div>


      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>


        <button disabled={!formIsValid} >Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
